let userScore=0,computerScore=0,drawScore=0;const userScore_span=document.getElementById("user-score"),computerScore_span=document.getElementById("computer-score"),drawScore_span=document.getElementById("draw-score"),scoreBoard_div=document.querySelector(".score-board"),result_p=document.querySelector(".results > p"),rock_div=document.getElementById("Rock"),paper_div=document.getElementById("Paper"),scissors_div=document.getElementById("Scissors"),lizard_div=document.getElementById("Lizard"),spock_div=document.getElementById("Spock"),smallUserWord="user".fontsize(3).sub(),smallCompWord="comp".fontsize(3).sub();function getComputerChoice(){return["Rock","Paper","Scissors","Lizard","Spock"][Math.floor(5*Math.random())]}function win(e,r){const s=document.getElementById(e);userScore++,userScore_span.innerHTML=userScore,computerScore_span.innerHTML=computerScore,drawScore_span.innerHTML=drawScore,s.classList.add("green-glow"),setTimeout(()=>s.classList.remove("green-glow"),500)}function lose(e,r){const s=document.getElementById(e);computerScore++,userScore_span.innerHTML=userScore,computerScore_span.innerHTML=computerScore,drawScore_span.innerHTML=drawScore,s.classList.add("red-glow"),setTimeout(()=>s.classList.remove("red-glow"),500)}function draw(e,r){drawScore++,userScore_span.innerHTML=userScore,computerScore_span.innerHTML=computerScore,drawScore_span.innerHTML=drawScore;const s=document.getElementById(e);s.classList.add("gray-glow"),setTimeout(()=>s.classList.remove("gray-glow"),500)}function game(e){const r=getComputerChoice();switch(e+r){case"RockLizard":case"RockScissors":result_p.innerHTML=e+smallUserWord+" crushes "+r+smallCompWord+". You Win!",win(e,r);break;case"PaperRock":result_p.innerHTML=e+smallUserWord+" covers "+r+smallCompWord+". You Win!",win(e,r);break;case"PaperSpock":result_p.innerHTML=e+smallUserWord+" disproves "+r+smallCompWord+". You Win!",win(e,r);break;case"ScissorsLizard":result_p.innerHTML=e+smallUserWord+" decapitates "+r+smallCompWord+". You Win!",win(e,r);break;case"ScissorsPaper":result_p.innerHTML=e+smallUserWord+" cuts "+r+smallCompWord+". You Win!",win(e,r);break;case"LizardSpock":result_p.innerHTML=e+smallUserWord+" poisons "+r+smallCompWord+". You Win!",win(e,r);break;case"LizardPaper":result_p.innerHTML=e+smallUserWord+" eats "+r+smallCompWord+". You Win!",win(e,r);break;case"SpockRock":result_p.innerHTML=e+smallUserWord+" vaporizes "+r+smallCompWord+". You Win!",win(e,r);break;case"SpockScissors":result_p.innerHTML=e+smallUserWord+" smashes "+r+smallCompWord+". You Win!",win(e,r);break;case"RockPaper":result_p.innerHTML=r+smallCompWord+" covers "+e+smallUserWord+". You Lost!",lose(e,r);break;case"RockSpock":result_p.innerHTML=r+smallCompWord+" vaporizes "+e+smallUserWord+". You Lost!",lose(e,r);break;case"PaperScissors":result_p.innerHTML=r+smallCompWord+" cuts "+e+smallUserWord+". You Lost!",lose(e,r);break;case"PaperLizard":result_p.innerHTML=r+smallCompWord+" eats "+e+smallUserWord+". You Lost!",lose(e,r);break;case"ScissorsRock":result_p.innerHTML=r+smallCompWord+" crushes "+e+smallUserWord+". You Lost!",lose(e,r);break;case"ScissorsSpock":result_p.innerHTML=r+smallCompWord+" smashes "+e+smallUserWord+". You Lost!",lose(e,r);break;case"LizardRock":result_p.innerHTML=r+smallCompWord+" crushes "+e+smallUserWord+". You Lost!",lose(e,r);break;case"LizardScissors":result_p.innerHTML=r+smallCompWord+" decapitates "+e+smallUserWord+". You Lost!",lose(e,r);break;case"SpockPaper":result_p.innerHTML=r+smallCompWord+" disproves "+e+smallUserWord+". You Lost!",lose(e,r);break;case"SpockLizard":result_p.innerHTML=r+smallCompWord+" poisons "+e+smallUserWord+". You Lost!",lose(e,r);break;case"RockRock":result_p.innerHTML="The rocks rubbed off, sparking. Songs were sung to the accompaniment of the campfire. Friendship won. Draw!",draw(e,r);break;case"PaperPaper":result_p.innerHTML="The papers were folded together to form a swan image. Art won. Draw!",draw(e,r);break;case"ScissorsScissors":result_p.innerHTML="Barbers used it to cut hair. Cleaning won. Draw!",draw(e,r);break;case"LizardLizard":result_p.innerHTML="Lizards snapped each other's tails. The war won. Draw!",draw(e,r);break;case"SpockSpock":result_p.innerHTML="Spock's talked forever. Intelligence won. Draw!",draw(e,r)}}function main(){rock_div.addEventListener("click",()=>game("Rock")),paper_div.addEventListener("click",()=>game("Paper")),scissors_div.addEventListener("click",()=>game("Scissors")),lizard_div.addEventListener("click",()=>game("Lizard")),spock_div.addEventListener("click",()=>game("Spock"))}main();