let userScore=0,computerScore=0,drawScore=0;const userScore_span=document.getElementById("user-score"),computerScore_span=document.getElementById("computer-score"),drawScore_span=document.getElementById("draw-score"),scoreBoard_div=document.querySelector(".score-board"),result_p=document.querySelector(".results > p"),rock_div=document.getElementById("Taş"),paper_div=document.getElementById("Kağıt"),scissors_div=document.getElementById("Makas"),lizard_div=document.getElementById("Kertenkele"),spock_div=document.getElementById("Spock"),smallUserWord="k".fontsize(3).sub(),smallCompWord="b".fontsize(3).sub();function getComputerChoice(){return["Taş","Kağıt","Makas","Kertenkele","Spock"][Math.floor(5*Math.random())]}function win(e,r){const a=document.getElementById(e);userScore++,userScore_span.innerHTML=userScore,computerScore_span.innerHTML=computerScore,drawScore_span.innerHTML=drawScore,a.classList.add("green-glow"),setTimeout(()=>a.classList.remove("green-glow"),500)}function lose(e,r){const a=document.getElementById(e);computerScore++,userScore_span.innerHTML=userScore,computerScore_span.innerHTML=computerScore,drawScore_span.innerHTML=drawScore,a.classList.add("red-glow"),setTimeout(()=>a.classList.remove("red-glow"),500)}function draw(e,r){drawScore++,userScore_span.innerHTML=userScore,computerScore_span.innerHTML=computerScore,drawScore_span.innerHTML=drawScore;const a=document.getElementById(e);a.classList.add("gray-glow"),setTimeout(()=>a.classList.remove("gray-glow"),500)}function game(e){const r=getComputerChoice();switch(e+r){case"TaşKertenkele":result_p.innerHTML=e+smallUserWord+" "+r+smallCompWord+"yi ezer. Kazandın!",win(e,r);break;case"TaşMakas":result_p.innerHTML=e+smallUserWord+" "+r+smallCompWord+"ı ezer. Kazandın!",win(e,r);break;case"KağıtTaş":result_p.innerHTML=e+smallUserWord+" "+r+smallCompWord+"ı kaplar. Kazandın!",win(e,r);break;case"KağıtSpock":result_p.innerHTML=e+smallUserWord+" "+r+smallCompWord+"'ı çürütür. Kazandın!",win(e,r);break;case"MakasKertenkele":result_p.innerHTML=e+smallUserWord+" "+r+smallCompWord+"nin başını keser. Kazandın!",win(e,r);break;case"MakasKağıt":result_p.innerHTML=e+smallUserWord+" "+r+smallCompWord+"ı keser. Kazandın!",win(e,r);break;case"KertenkeleSpock":result_p.innerHTML=e+smallUserWord+" "+r+smallCompWord+"'ı zehirler. Kazandın!",win(e,r);break;case"KertenkeleKağıt":result_p.innerHTML=e+smallUserWord+" "+r+smallCompWord+"ı yer. Kazandın!",win(e,r);break;case"SpockTaş":result_p.innerHTML=e+smallUserWord+" "+r+smallCompWord+"ı buharlaştırır. Kazandın!",win(e,r);break;case"SpockMakas":result_p.innerHTML=e+smallUserWord+" "+r+smallCompWord+"ı dağıtır. Kazandın!",win(e,r);break;case"TaşKağıt":result_p.innerHTML=r+smallCompWord+" "+e+smallUserWord+"ı kaplar. Kaybettin!",lose(e,r);break;case"TaşSpock":result_p.innerHTML=r+smallCompWord+" "+e+smallUserWord+"ı buharlaştırır. Kaybettin!",lose(e,r);break;case"KağıtMakas":result_p.innerHTML=r+smallCompWord+" "+e+smallUserWord+"ı keser. Kaybettin!",lose(e,r);break;case"KağıtKertenkele":result_p.innerHTML=r+smallCompWord+" "+e+smallUserWord+"ı yer. Kaybettin!",lose(e,r);break;case"MakasTaş":result_p.innerHTML=r+smallCompWord+" "+e+smallUserWord+"ı ezer. Kaybettin!",lose(e,r);break;case"MakasSpock":result_p.innerHTML=r+smallCompWord+" "+e+smallUserWord+"ı dağıtır. Kaybettin!",lose(e,r);break;case"KertenkeleTaş":result_p.innerHTML=r+smallCompWord+" "+e+smallUserWord+"yi ezer. Kaybettin!",lose(e,r);break;case"KertenkeleMakas":result_p.innerHTML=r+smallCompWord+" "+e+smallUserWord+"nin başını keser. Kaybettin!",lose(e,r);break;case"SpockKağıt":result_p.innerHTML=r+smallCompWord+" "+e+smallUserWord+"'ı çürütür. Kaybettin!",lose(e,r);break;case"SpockKertenkele":result_p.innerHTML=r+smallCompWord+" "+e+smallUserWord+"'ı zehirler. Kaybettin!",lose(e,r);break;case"TaşTaş":result_p.innerHTML="Taşlar sürtünerek kıvılcım çıkarttı. Şarkılar eşliğinde kamp ateşi yakıldı. Dostluk kazandı. Berabere!",draw(e,r);break;case"KağıtKağıt":result_p.innerHTML="Kağıtlar birlikte katlanarak kuğu görüntüsü aldı. Sanat kazandı. Berabere!",draw(e,r);break;case"MakasMakas":result_p.innerHTML="Berberler saç kesmek için kullandı. Temizlik kazandı. Berabere!",draw(e,r);break;case"KertenkeleKertenkele":result_p.innerHTML="Kertenkeleler birbirlerinin kuyruğunu koparttı. Savaş kazandı. Berabere!",draw(e,r);break;case"SpockSpock":result_p.innerHTML="Spocklar sonsuza dek konuşmaya başladı.Bilgi kazandı. Berabere!",draw(e,r)}}function main(){rock_div.addEventListener("click",()=>game("Taş")),paper_div.addEventListener("click",()=>game("Kağıt")),scissors_div.addEventListener("click",()=>game("Makas")),lizard_div.addEventListener("click",()=>game("Kertenkele")),spock_div.addEventListener("click",()=>game("Spock"))}main();